import React from "react";
import { useState, useEffect } from "react";

import ModalPreloader from "../../modal preloader/ModalPreloader";

import styles from "./Button.module.css";
import modalStyles from "./Modal.module.css";

import { Modal, Button } from "react-bootstrap";

import numeral from "numeral";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faClock } from "@fortawesome/free-solid-svg-icons";

const API_BASE = "http://localhost:3001";
let localTime;
let sunrise;
let sunset;
let gmtOffset;

const TimeZoneBtn = (props) => {
  const [modalShow, setModalShow] = useState(false);
  const [tzData, setTzData] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => {
    // For loading the preloader
    console.log(tzData);
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  }, [tzData]);

  const tzBtnHandler = async () => {
    setModalShow(true);

    await fetch(`${API_BASE}/timezone/${props.geoJsonData.properties.iso_a2}`)
      .then((res) => res.json())
      .then((data) => {
        setTzData(data);
      })
      .catch((e) => console.error("Error: ", e));
  };

  const handleModalClose = () => {
    setModalShow(false);
    setTzData(false);
  };

  if (tzData) {
    let localTime = tzData.time.split(" ")[1];
    let sunrise = tzData.sunrise.split(" ")[1];
    let sunset = tzData.sunset.split(" ")[1];
    let gmtOffset = tzData.gmtOffset;
  }

  return (
    <>
      <button className={styles.tzBtn} onClick={tzBtnHandler}>
        <FontAwesomeIcon icon={faClock} />
      </button>

      {tzData && (
        <Modal
          show={modalShow}
          onHide={handleModalClose}
          animation={true}
          centered
        >
          {" "}
          <ModalPreloader loading={loading} />
          <Modal.Header className={modalStyles.basicDataModal}>
            <Modal.Title
              className={modalStyles.basicDataTitle}
            >{`Overview - ${props.geoJsonData.properties.name}`}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
            <div class="row justify-content-between">
              <div class="col-5 text-start">
                <p id="tzOffset" class="fs-6 text-secondary mb-0"></p>
                <p id="tzCity" class="fs-4 mb-1"></p>
                <div class="row text-secondary fs-6">
                  <div class="col-6">
                    <p class="p-0" id="sunrise"></p>
                  </div>
                  <div class="col-6">
                    <p class="p-0" id="sunset"></p>
                  </div>
                </div>
              </div>
              <div class="col-7 pt-4">
                <p id="localTime" class="fs-1 fw-light"></p>
              </div>
            </div>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="outline-primary" onClick={handleModalClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      )}
    </>
  );
};

export default TimeZoneBtn;
