import React from "react";
import { useState, useEffect } from "react";

import ModalPreloader from "../../modal preloader/ModalPreloader";

import styles from "./Button.module.css";
import modalStyles from './Modal.module.css'


import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import numeral from "numeral";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCloud} from '@fortawesome/free-solid-svg-icons'

const API_BASE = 'http://localhost:3001'

const WeatherBtn = (props) => {
  const [modalShow, setModalShow] = useState(false);
  const [weatherData, setWeatherData ] = useState(false);
  const [loading, setLoading] = useState(false);

  console.log(props.geoJsonData)

  useEffect(() => { // For loading the preloader
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2000)
  },[weatherData])



  const weatherBtnHandler = async () => {
    setModalShow(true);
    // Get capital City
    const capitalCity = await fetch(`${API_BASE}/capital-city/${props.geoJsonData.properties.iso_a2}`)
    .then(res => res.json())
    .catch(e => console.error('Error', e))

    // Get Weather data
    const weather = await fetch(`${API_BASE}/weather/${capitalCity}`)
    .then(res => res.json())
    .then(data => {
      console.log(data);
        let icon = data.forecast[0].day.condition.icon;
        let icon1 = data.forecast[1].day.condition.icon;
        let icon2 = data.forecast[2].day.condition.icon;

        console.log(icon)
        console.log(icon1)
        console.log(icon2)

    });

    // setWeatherData(weather)
  };

  const handleModalClose = () => {
    setModalShow(false);
    setWeatherData(false);

  }

  return (
    <>
      <button className={styles.weatherBtn} onClick={weatherBtnHandler}>
        <FontAwesomeIcon icon={faCloud}/>
      </button>

      {weatherData && <Modal 
      show={modalShow} 
      onHide={handleModalClose} 
      animation={true} 
      centered
      > <ModalPreloader loading={loading} />
        <Modal.Header className={modalStyles.basicDataModal}>
          <Modal.Title className={modalStyles.basicDataTitle}>Weather</Modal.Title>
        </Modal.Header>
        <Modal.Body>
          <p>weather</p>

        </Modal.Body>
        <Modal.Footer>
          <Button variant="outline-primary" onClick={handleModalClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal> }
    </>
  );
};

export default WeatherBtn;
