  setLocation({latitude, longitude})
}




  return (
    <div className="App">
      {/* <Preloader loading={loading} /> */}
      <Map />
      {/* <p>{location.latitude}</p>
      <p>{location.longitude}</p> */}


    </div>
  );
}

export default App;
