import React from "react";
import { useState, useEffect } from "react";

import ModalPreloader from "../../modal preloader/ModalPreloader";

import styles from "./Button.module.css";
import modalStyles from './Modal.module.css'

import { Container, Row, Col, Image, Button, Modal } from 'react-bootstrap'
import numeral from "numeral";
import { FontAwesomeIcon } from '@fortawesome/react-fontawesome'
import { faCloud} from '@fortawesome/free-solid-svg-icons'

const API_BASE = 'http://localhost:3001'

const WeatherBtn = (props) => {

  const [modalShow, setModalShow] = useState(false);
  const [weatherData, setWeatherData] = useState(false);
  const [loading, setLoading] = useState(false);

  useEffect(() => { // For loading the preloader
    console.log(weatherData)
    setLoading(true);
    setTimeout(() => {
      setLoading(false);
    }, 2000)
  },[weatherData])



  const weatherBtnHandler = async () => {
    setModalShow(true);
    // Get capital City
    const capitalCity = await fetch(`${API_BASE}/capital-city/${props.geoJsonData.properties.iso_a2}`)
    .then(res => res.json())
    .catch(e => console.error('Error', e))

    // Get Weather data
    const weather = await fetch(`${API_BASE}/weather/${capitalCity}`)
    .then(res => res.json())
    .catch(e => console.error('Error', e));

    setWeatherData(weather);
  };



  const handleModalClose = () => {
    setModalShow(false);
    setWeatherData(false);
  }

  if (weatherData) {
    let icon = weatherData.forecast.forecastday[0].day.condition.icon;
    let icon1 = weatherData.forecast.forecastday[1].day.condition.icon;
    let icon2 = weatherData.forecast.forecastday[2].day.condition.icon;

    let dayData = weatherData.forecast.forecastday[0].date.toString();
    let dayDate = new Date(dayData);
    let dayOfWeek = dayDate.toLocaleString("en-GB", {
      weekday: "short",
    });
    let dayOfMonth = dayDate.toLocaleString("en-GB", {
      day: "numeric",
      month: "short",
    });

    let day1Data = weatherData.forecast.forecastday[1].date.toString();
    let day1Date = new Date(day1Data);
    let dayOfWeek1 = day1Date.toLocaleString("en-GB", {
      weekday: "short",
    });
    let dayOfMonth1 = day1Date.toLocaleString("en-GB", {
      day: "numeric",
      month: "short",
    });

    let day2Data = weatherData.forecast.forecastday[2].date.toString();
    let day2Date = new Date(day2Data);
    let dayOfWeek2 = day2Date.toLocaleString("en-US", {
      weekday: "short",
    });
    let dayOfMonth2 = day2Date.toLocaleString("en-US", {
      day: "numeric",
      month: "short",
    });

    let lastUpdated = weatherData.current.last_updated;
    let dateTime = new Date(lastUpdated);
    let hours = dateTime.getHours().toString().padStart(2, "0");
    let minutes = dateTime.getMinutes().toString().padStart(2, "0");
    let todayDateData = weatherData.forecast.forecastday[0].date.toString();
    let todayDate = new Date(todayDateData);
    let todayDayOfMonth = todayDate.toLocaleString("en-US", {
      day: "numeric",
      month: "short",
    });
  };


  return (
    <>
      <button className={styles.weatherBtn} onClick={weatherBtnHandler}>
        <FontAwesomeIcon icon={faCloud}/>
      </button>

      {weatherData && <Modal 
      show={modalShow} 
      onHide={handleModalClose} 
      animation={true} 
      centered
      > <ModalPreloader loading={loading} />
        <Modal.Header className={modalStyles.basicDataModal}>
          <Modal.Title className={modalStyles.basicDataTitle}>Weather</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Container className="m-2">
      <Row>
        <Col className="border m-2">
          <p className="fw-bold fs-5 mt-1">TODAY</p>
          <Row>
            <Col className="text-center m-3">
              <p className="fw-bold fs-6">{}</p>
            </Col>
            <Col className="text-center">
              <Image fluid id="todayIcon" src="" alt="" title="" />
            </Col>
            <Col className="text-center">
              <p className="fw-bold fs-4 mb-0">
                <span id="todayMaxTemp"></span>
                <sup>o</sup><span className="tempMetric">c</span>
              </p>
              <p className="fs-5 mt-0 text-secondary">
                <span id="todayMinTemp">-</span>
                <sup>o</sup><span className="tempMetric">c</span>
              </p>
            </Col>
          </Row>
        </Col>
      </Row>


    </Container>

        </Modal.Body>
        <Modal.Footer>
          <Button variant="outline-primary" onClick={handleModalClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal> }
    </>
  );
};

export default WeatherBtn;
