import React from "react";
import { useState, useEffect, useReducer } from "react";

import ModalPreloader from "../../modal preloader/ModalPreloader";

import styles from "./Button.module.css";
import modalStyles from "./Modal.module.css";

import { Button, Modal } from "react-bootstrap";
import numeral from "numeral";
import { FontAwesomeIcon } from "@fortawesome/react-fontawesome";
import { faDollarSign } from "@fortawesome/free-solid-svg-icons";

const API_BASE = "http://localhost:3001";

const ExchangeRateBtn = (props) => {

  let countryCode;
  let me

  const [modalShow, setModalShow] = useState(false);
  const [loading, setLoading] = useState(false);

  //Form state
  const [converted, setConverted] = useState(0);
  const [total, setTotal] = useState(0);
  const [value, setValue] = useState(1);
  const [currencyName, setCurrencyName] = useState('');


  useEffect(() => {
    setLoading(true);
    console.log(converted)
    setTimeout(() => {
      setLoading(false);
    }, 2000);
  },[currencyName]);

  const erBtnHandler = async () => {
    setModalShow(true);

    await fetch(
      `${API_BASE}/currency/${props.geoJsonData.properties.iso_a2}`
    )
      .then((res) => res.json()).
      then((data) => {
        countryCode = Object.keys(data)[0];
        setCurrencyName(data[countryCode].name)
      })
      .catch((e) => console.error("Error", e));

    await fetch(
        `${API_BASE}/er/`
      )
        .then((res) => res.json())
        .then((data) => {
          setTotal(numeral(data.rates[countryCode]).format('0.0'));
          setConverted(numeral(data.rates[countryCode]).format('0.0'));

        })
        .catch((e) => console.error("Error", e));   
  };

  const handleModalClose = () => {
    setModalShow(false);
    setCurrencyName(false);

  };

  const changeCurrencyHandler = (value) => {
    setValue(value);
    setTotal(value * converted)
  }



  return (
    <>
      <button className={styles.erBtn} onClick={erBtnHandler}>
        <FontAwesomeIcon icon={faDollarSign} />
      </button>

      {currencyName && (
        <Modal
          show={modalShow}
          onHide={handleModalClose}
          animation={true}
          centered
        >
          {/* <ModalPreloader loading={loading} /> */}
          <Modal.Header className={modalStyles.basicDataModal}>
            <Modal.Title
              className={modalStyles.basicDataTitle}
            >{`Exchange Rate - ${props.geoJsonData.properties.name}`}</Modal.Title>
          </Modal.Header>
          <Modal.Body>
           <form>
                <div className="form-floating mb-3">
                  <input
                    id="fromValue"
                    type="number"
                    className="form-control"
                    value={value}
                    min="1"
                    step="1"
                    onChange={(e) => {
                      changeCurrencyHandler(e.target.value)
                    }}
                  />
                  <label htmlFor="fromValue">From USD</label>
                </div>

                <div className="form-floating mb-3">
                  <input id="exchangeRate" className="form-control" disabled value={currencyName} />
                  <label htmlFor="exchangeRate">Convert to</label>
                </div>

                <div className="form-floating">
                  <input
                    id="erResult"
                    type="text"
                    className="form-control"
                    disabled
                    value={total}
                  />
                  <label htmlFor="erResult">Result</label>
                </div>
              </form>
          </Modal.Body>
          <Modal.Footer>
            <Button variant="outline-primary" onClick={handleModalClose}>
              Close
            </Button>
          </Modal.Footer>
        </Modal>
      )}
    </>
  );
};

export default ExchangeRateBtn;
