import React from "react";
import styles from "./Button.module.css";
import { useState } from "react";

import Button from "react-bootstrap/Button";
import Modal from "react-bootstrap/Modal";
import Table from 'react-bootstrap/Table';
import numeral from "numeral";

import infoIcon from "../../../assets/info.png";

const API_BASE = 'http://localhost:3001'

const BasicDataBtn = (props) => {
  const [modalShow, setModalShow] = useState(false);
  const [basicData, setBasicData ] = useState(false);
  const [flag, setFlag] = useState(false);

console.log(props.geoJsonData)
  const handleModalClose = () => {
    setModalShow(false)
  }

  const basicDataBtnHandler = async () => {
    setModalShow(true);
    const data  = await fetch(`${API_BASE}/basic-data/${props.geoJsonData.properties.iso_a2}`)
    .then(res => res.json())
    .catch(e => console.error('Error: ', e));

    setBasicData(data.geonames[0]);

    const flag = await fetch(`${API_BASE}/flag-data/${props.geoJsonData.properties.name}`)
    .then(res => res.json())
    .catch(e => console.error('Error', e))

    setFlag(flag);


    // add data to modal

  };

  return (
    <>
      <button className={styles.button}>
        <img
          src={infoIcon}
          className={styles.icon}
          onClick={basicDataBtnHandler}
        />
      </button>

      { basicData && <Modal show={modalShow} onHide={handleModalClose} animation={true} centered>
        <Modal.Header closeButton>
          <Modal.Title>{`Overview - ${props.geoJsonData.properties.name}`}</Modal.Title>
        </Modal.Header>
        <Modal.Body>
        <Table striped hover responsive>
      <tbody>
        <tr>
          <td>1</td>
          <td>Capital City</td>
          <td>{basicData.capital}</td>
        </tr>

        <tr>
          <td>2</td>
          <td>Continent</td>
          <td>{basicData.continentName}</td>
        </tr>

        <tr>
          <td>3</td>
          <td>Population</td>
          <td>{numeral(basicData.population).format('0,0')}</td>
        </tr>

        <tr>
          <td>3</td>
          <td>Area</td>
          <td>{`${numeral(basicData.areaInSqKm).format('0,0')} km`}</td>
        </tr>

        <tr>
          <td>3</td>
          <td>Flag</td>
          <td>{flag.flag}</td>
        </tr>


      </tbody>
    </Table>

        </Modal.Body>
        <Modal.Footer>
          <Button variant="secondary" onClick={handleModalClose}>
            Close
          </Button>
        </Modal.Footer>
      </Modal> }
    </>
  );
};

export default BasicDataBtn;
