import React from "react";
import { useState, useEffect } from "react";
import {
  MapContainer,
  GeoJSON,
} from "react-leaflet";

import styles from "./Map.module.css";
import "leaflet/dist/leaflet.css";

import List from "./list/List";
import MapLayers from "./mapLayers/MapLayers";

const API_BASE = "http://localhost:3001";



const Map = (props) => {

    // Getting country data for select
    const [countryData, setCountryData] = useState(null);

    const getCountries = async () => {
      const data = await fetch(`${API_BASE}/country-data`)
        .then((res) => res.json())
        .then((data) => {
          setCountryData(data);  
        })
        .catch((err) => console.error("Error: ", err));    
    }

  useEffect(() => {
    getCountries();
  }, []);



 // Getting the border
 const [border, setBorder] = useState(null);

  const borderHandler = (x) => {
    setBorder(x);
  };

  // Getting Airport data
  const airportsHandler = async (countryCode) => {
    fetch(`${API_BASE}/city-data`, {
      method: 'POST',
      headers: {
      "Content-Type": 'application/json'
      },
      body: JSON.stringify({
        countryCode
      }).then((res) => res.json())
      .then((data) => {
        console.log(data)
        // use data for markers
      })
    })
  }

  return (
    <>
      <MapContainer center={[0, 0]} zoom={2} scrollWheelZoom={true} minZoom={1} >
        <List borderHandler={borderHandler} 
        countryData={countryData}
        getAirports={airportsHandler} />
        <MapLayers />
        <GeoJSON data={border ? border : ""} key={JSON.stringify(border)} style={{
          color:'red',
          weight: 1,
          opacity: 1}
        } />
      </MapContainer>
    </>
  );
};

export default Map;
